package com.hsd.asmfsx.utils;import java.text.DateFormat;import java.text.ParseException;import java.text.SimpleDateFormat;import java.util.Date;public class DateFormatUtils {	public static String FormatDate2String(Date birthday){		SimpleDateFormat format = new SimpleDateFormat("yyyy-MM-dd");		return format.format(birthday);	}	public static Date FormatString2Date(String string){		SimpleDateFormat format = new SimpleDateFormat("yyyy-MM-dd");		Date date = null;		try {			 date = format.parse(string);		} catch (ParseException e) {			e.printStackTrace();		}		return date;	}}